mapper.select:
SELECT * FROM GARADA
WHERE Code = #{id}
UNION ALL
SELECT * FROM CITILANK
WHERE Code = #{id}
UNION ALL
SELECT * FROM SRIJAYA
WHERE Code = #{id};

mapper.update:
UPDATE GARADA SET NumberBaggageAvailable = #{numberBaggage},
NumberSeatAvailable = #{numberSeat},
SeatRegularZone = #{seatRegularZone},
SeatGreenZone = #{seatGreenZone},
SeatSold = #{seatSold}
WHERE Code = #{code};

UPDATE CITILANK SET NumberBaggageAvailable = #{numberBaggage},
NumberSeatAvailable = #{numberSeat},
SeatRegularZone = #{seatRegularZone},
SeatGreenZone = #{seatGreenZone},
SeatSold = #{seatSold}
WHERE Code = #{code};

UPDATE SRIJAYA SET NumberBaggageAvailable = #{numberBaggage},
NumberSeatAvailable = #{numberSeat},
SeatRegularZone = #{seatRegularZone},
SeatGreenZone = #{seatGreenZone},
SeatSold = #{seatSold}
WHERE Code = #{code};

mapper.select:
SELECT SeatRegularZone FROM GARADA
WHERE Code = #{id}
UNION ALL
SELECT SeatRegularZone FROM CITILANK
WHERE Code = #{id}
UNION ALL
SELECT SeatRegularZone FROM SRIJAYA
WHERE Code = #{id};

SELECT SeatGreenZone FROM GARADA
WHERE Code = #{id}
UNION ALL
SELECT SeatGreenZone FROM CITILANK
WHERE Code = #{id}
UNION ALL
SELECT SeatGreenZone FROM SRIJAYA
WHERE Code = #{id};

SELECT SeatSold FROM GARADA
WHERE Code = #{id}
UNION ALL
SELECT SeatSold FROM CITILANK
WHERE Code = #{id}
UNION ALL
SELECT SeatSold FROM SRIJAYA
WHERE Code = #{id};
